import 'package:dio/dio.dart';class AddCategoryPram {  AddCategoryPram({      this.nameAr,       this.nameEn,       this.image,});  AddCategoryPram.fromJson(dynamic json) {    nameAr = json['name_ar'];    nameEn = json['name_en'];    image = json['image'];  }  String? nameAr;  String? nameEn;  String? image;  Map<String, dynamic> toJson() {    final map = <String, dynamic>{};    map['name_ar'] = nameAr;    map['name_en'] = nameEn;    map['image'] = MultipartFile.fromFileSync(image!);    return map;  }  AddCategoryPram copyWith({    String? nameAr,    String? nameEn,    String? image,  }) {    return AddCategoryPram(      nameAr: nameAr ?? this.nameAr,      nameEn: nameEn ?? this.nameEn,      image: image ?? this.image,    );  }}