class CategoryData {  CategoryData({      this.id,       this.nameAr,       this.nameEn,       this.image,       this.userId,       this.postsCount,});  CategoryData.fromJson(dynamic json) {    id = json['id'];    nameAr = json['name_ar'];    nameEn = json['name_en'];    image = json['image'];    userId = json['user_id'];    postsCount = json['posts_count'];  }  int ?id;  String? nameAr;  String ?nameEn;  String ?image;  int? userId;  int ?postsCount;  Map<String, dynamic> toJson() {    final map = <String, dynamic>{};    map['id'] = id;    map['name_ar'] = nameAr;    map['name_en'] = nameEn;    map['image'] = image;    map['user_id'] = userId;    map['posts_count'] = postsCount;    return map;  }}